def threeSum(nums):
    """
    :type nums: List[int]
    :rtype: List[List[int]]
    """

    """
    Logic:
    Sorting the array first
    later checking if nums[i] is sum of two numbers in the remainder of the array. 
    """

    counter = len(nums)
    if counter < 2:
        return []
    nums = sorted(nums)
    answer = []

    for i in range(0, counter):
        if nums[i] > 0:
            return answer
        # as we previously calculated for the last element, if its the same, we do not need to recalculate
        if i > 0 and nums[i] == nums[i-1]:
            continue
        j = i + 1
        k = counter - 1
        while j < k:
            total = nums[i] + nums[j] + nums[k]
            if total == 0:
                answer.append([nums[i] ,nums[j], nums[k]])
                j += 1
                k -= 1
                # as repetition is not allowed, we just move ahead if we find similar elements 
                while j < k and nums[j] == nums[j-1]: j += 1
                while j < k and nums[k] == nums[k + 1]: k -= 1
            else:
                if total > 0:
                    k -= 1
                else:
                    j += 1

    return answer

